mapper.select:
select count(*)as count from tb_admin

    and admin_name like concat('%',#{adminName},'%')
    and reg_time >=#{startTime}
    and reg_time <= #{endTime}
    or role_id = 1 or role_id = 2

select * from tb_admin a left join tb_role r on
r.role_id=a.role_id

    and admin_name like
        concat('%',#{admin.adminName},'%')

    and reg_time >=#{admin.startTime}

    and reg_time <= #{admin.endTime}
    or a.role_id = 1 or a.role_id = 2

limit #{start},#{pageSize};

mapper.update:
update tb_admin

    admin_name=#{adminName},
    admin_sex=#{adminSex},
    role_id=#{roleId},
    admin_tel=#{adminTel}

where admin_id=#{adminId}
